import React from "react";
import { connect } from "react-redux";
import Axios from "axios";
import { setActiveUsers } from "../redux/actions/usersActions";

const ActiveUsers = ({ dispatch,username,activeUsers }) => {
    React.useEffect(()=>{
        Axios.get('/authserver/')
        .then((res)=>{
            dispatch(setActiveUsers(res.data.counter))
        })
    },[dispatch])
    return (
        <div>
            <p>Active users: {activeUsers}</p>
        </div>
    );
};
const mapStateToProps = state => ({
    username: state.userReducer.username,
    activeUsers: state.userReducer.activeUsers,
});

export default connect(mapStateToProps)(ActiveUsers);
