import React, { useState } from "react";
import { connect } from "react-redux";
import { getNotes, addNote,deleteNote,setListNotes, setNewNote } from "../redux/actions/notesActions";

const Note = ({ dispatch, notes, newNote }) => {


    React.useEffect(() => {
        dispatch(getNotes());
    }, [dispatch]);

    return (
        <div>
            <div>
                <h1>Make a new note!</h1>
                <p>Description</p>
                <input
                    onChange={e => dispatch(setNewNote(e.target.value))}
                    name="description"
                    type="text"
                ></input>

                <button
                    onClick={() => dispatch(addNote(newNote))}
                    type="button"
                >
                    ADD
                </button>
            </div>

            {notes.map((note) => (

                <div key={note._id}>
                    <p>{note._id}</p>
                    <p>{note.description}</p>
                    <button onClick={()=>dispatch(deleteNote(note._id))}>Delete</button>
                </div>
            ))}
        </div>
    );
};

const mapStatetoProps = state => ({
    notes: state.notesReducer.notes,
    newNote: state.notesReducer.newNote
});

export default connect(mapStatetoProps)(Note);
