const initialState = {
    notes:[],
    newNote: 'enter new note here',
    loading: false
}

const notesReducer = (state = initialState, action) =>{
    switch(action.type){
        case 'SET_LIST_NOTES':
            return {
                ...state,
                notes:action.notes
            }
        case 'ADD_NOTE':
            return{
                ...state,
                notes:[action.note,...state.notes]
            }
        case 'DELETE_NOTE':
            return{
                ...state,
                notes:state.notes.filter(note => note._id !== action.id)
            }
        case 'SET_NEW_NOTE':
            return{
                ...state,
                newNote: action.newNote,
            }
        case 'SET_ID':
            return{
                ...state,
                _id: action._id
            }
        default:
            return state
        }   
}

export default notesReducer;
