import axios from "axios";

export const getNotes = () => (dispatch, getState) => {
    const proxyUrl = "http://localhost:3004";
    axios
        .get(`/allNotes`)
        .then(res => {
            dispatch(setListNotes(res.data));
        })
        .then(console.log);
};

export const addNote = newNote => (dispatch, getState) => {
    const { newNote } = getState().notesReducer;
    axios
        .get(`/addNote?newNote=${newNote}`)
        .then(res => {
            dispatch({
                type: "ADD_NOTE",
                newNote: res.data
            });
            dispatch(setNewNote(""));
            dispatch(setId(""));
            dispatch(getNotes());
        })
        .then(console.log);
};

export const deleteNote = () => (dispatch, getState) => {
    const { _id } = getState().notesReducer;
    axios
        .delete(`/deleteNote/${_id}`, { params: { _id: _id } })
        .then(res => {
            dispatch({
                type: "DELETE_NOTE",
                _id
            });
            dispatch(setNewNote(""));
            dispatch(setId(""));
            dispatch(getNotes());
        })
        .catch(err => {
            console.log(err);
        });
};

export const setListNotes = notes => ({
    type: "SET_LIST_NOTES",
    notes
});

export const setId = _id => ({
    type: "SET_ID",
    _id
});

export const setNewNote = newNote => ({
    type: "SET_NEW_NOTE",
    newNote
});

export const updateNote = () => (dispatch, getState) => {
    const { _id, newNote } = getState().notesReducer;

    axios
        .get(`/update?_id=${_id}&notes=${newNote}`)
        .then(() => {
            dispatch(setNewNote(""));
            dispatch(setId(""));
            dispatch(getNotes());
        })
        .catch(console.log);
};
