import { connect } from "react-redux";
import { getNotes,addNote,setId,setNewNote,updateNote,deleteNote } from "../redux/actions/notesActions";

const Note = ({dispatch,newNote,notes,_id }) => {

    React.useEffect(()=>{
        dispatch(getNotes())
    },[])

    const handleSubmit = (e) =>{
        e.preventDefault()

        const newItem = {
            description: newNote
        }

        dispatch(addNote(newItem))
    }

