import React, { useState } from "react";
import { connect } from "react-redux";
import { getNotes, addNote, setNewNote } from "../redux/actions/notesActions";

const Note = ({ dispatch, notes, newNote }) => {


    React.useEffect(() => {
      const listNotes = () =>{
        dispatch(getNotes())
      }
      listNotes()
    }, [dispatch,notes]);

    return (
        <div>
            <div>
                <h1>Make a new note!</h1>
                <p>Description</p>
                <input
                    onChange={e => dispatch(setNewNote(e.target.value))}
                    name="description"
                    type="text"
                ></input>

                <button
                    onClick={() => dispatch(addNote(newNote))}
                    type="button"
                >
                    ADD
                </button>
            </div>

            {notes.map((note, i) => (
                <div key={i}>
                    <p>{note.description}</p>
                </div>
            ))}
        </div>
    );
};

const mapStatetoProps = state => ({
    notes: state.notesReducer.notes,
    newNote: state.notesReducer.newNote
});

export default connect(mapStatetoProps)(Note);
