import React, { useState } from "react";
import { connect } from "react-redux";
import {
    getNotes,
    addNote,
    deleteNote,
    setId,
    setNewNote,
    setActionType
} from "../redux/actions/notesActions";

const Note = ({ dispatch, notes, newNote,actionType }) => {

    React.useEffect(() => {
        dispatch(getNotes());
    }, [notes.length]);

    return (
        <div>
            <div>
                <h1>Make a new note!</h1>
                <input type="text" placeholder="enter id here"></input>
                <input
                    onChange={e => dispatch(setNewNote(e.target.value))}
                    name="description"
                    placeholder = "enter new note here"
                    value={newNote}
                    type="text"
                ></input>

                <button
                    onClick={() => dispatch(addNote(newNote))}
                    type="button"
                >
                    Add Note
                </button>
            </div>
            <table>
                <tr>
                    <th>id</th>
                    <th>Description</th>
                    <th>User</th>
                    <th>Actions</th>
                </tr>
                {notes.map(note => (
                    <tr key={note._id}>
                        <td width="35%">{note._id}</td>
                        <td width="50%">{note.description}</td>
                        <td width="10%"></td>
                        <td width="5%">
                            <button>Edit</button>
                            <button
                                onClick={() => {
                                    dispatch(setId(note._id));
                                    dispatch(deleteNote(note._id));
                                }}
                            >
                                Delete
                            </button>
                        </td>
                    </tr>
                ))}
            </table>
        </div>
    );
};

const mapStatetoProps = state => ({
    notes: state.notesReducer.notes,
    actionType: state.notesReducer.actionType,
    _id: state.notesReducer._id,
    newNote: state.notesReducer.newNote
});

export default connect(mapStatetoProps)(Note);
