import axios from "axios";

const proxyUrl = "http://localhost:3004";


export const getNotes = () => (dispatch, getState) => {
    axios
        .get(`${proxyUrl}/allNotes`)
        .then(res => {
            dispatch(setListNotes(res.data));
        })
        .then(console.log);
};

export const addNote = newNote => (dispatch, getState) => {
    const { newNote } = getState().notesReducer;
    axios
        .get(`${proxyUrl}/addNote?newNote=${newNote}`)
        .then(() => {
            
            dispatch(setNewNote(""));
            dispatch(setId(""));
            dispatch(getNotes());
        })
        .then(console.log);
};

export const deleteNote = () => (dispatch, getState) => {
    const { _id } = getState().notesReducer;
    axios
        .delete(`/deleteNote/${_id}`, { params: { _id: _id } })
        .then(res => {
            dispatch({
                type: "DELETE_NOTE",
                _id
            });
            dispatch(setNewNote(""));
            dispatch(setId(""));
            dispatch(getNotes());
        })
        .catch(err => {
            console.log(err);
        });
};

export const updateNote = () => (dispatch,getState) =>{
    const {_id,newNote} = getState().notesReducer
    axios.get(`/update?id=${_id}&newNote=${newNote}`)
    .then(()=>{
        dispatch(setActionType('Add'))
        dispatch(setNewNote(''))
        dispatch(setId(''))
        dispatch(getNotes())
    })
}

export const setListNotes = notes => ({
    type: "SET_LIST_NOTES",
    notes
});

export const setId = _id => ({
    type: "SET_ID",
    _id
});

export const setNewNote = newNote => ({
    type: "SET_NEW_NOTE",
    newNote
});

export const setActionType = (actionType) =>({
    type: 'SET_ACTION_TYPE',
    actionType,
})
