import axios from "axios";

export const getNotes = () => (dispatch) =>{
  dispatch(setNotesLoading())
  axios.get('/notes')
  .then(res =>dispatch(setListNotes(res.data)))
  .then(console.log)
}

export const addNote = note => (dispatch) =>{
  axios.post('/notes',note)
  .then(res =>{
    dispatch({
      type: 'ADD_NOTE',
      note: res.data
    })
  })
}

export const deleteNote = () =>({
  type: 'DELETE_NOTE',
})

export const setNotesLoading =() =>({
    type: 'NOTES_LOADING',
})

export const setListNotes = notes => ({
  type: "SET_LIST_NOTES",
  notes
});

export const setId = _id => ({
  type: "SET_ID",
  _id
});

export const setNewNote = newNote => ({
  type: "SET_NEW_NOTE",
  newNote
});

export const updateNote = () => (dispatch, getState) => {
  const { _id, newNote } = getState().notesReducer;

  axios.get(`/update?_id=${_id}&notes=${newNote}`)
    .then(() => {
      dispatch(setNewNote(""));
      dispatch(setId(""));
      dispatch(getNotes());
    })
    .catch(console.log);
};
