import React, { useState } from "react";
import { connect } from "react-redux";
import {
    getNotes,
    addNote,
    deleteNote,
    setId,
    setNewNote
} from "../redux/actions/notesActions";

const Note = ({ dispatch, notes, newNote }) => {
    React.useEffect(() => {
        dispatch(getNotes());
    }, [notes.length]);

    return (
        <div>
            <div>
                <h1>Make a new note!</h1>
                <p>Description</p>
                <input
                    onChange={e => dispatch(setNewNote(e.target.value))}
                    name="description"
                    type="text"
                ></input>

                <button
                    onClick={() => dispatch(addNote(newNote))}
                    type="button"
                >
                    ADD
                </button>
            </div>
            <table>
                <tr>
                    <th>id</th>
                    <th>Description</th>
                    <th>User</th>
                    <th>Edit</th>
                    <th>Delete</th>
                </tr>
                {notes.map(note => (
                    <tr key={note._id}>
                        <td>{note._id}</td>
                        <td>{note.description}</td>
                        <td></td>
                        <td></td>
                        <button
                            onClick={() => {
                                dispatch(setId(note._id));
                                dispatch(deleteNote(note._id));
                            }}
                        >
                            Delete
                        </button>
                    </tr>
                ))}
            </table>
        </div>
    );
};

const mapStatetoProps = state => ({
    notes: state.notesReducer.notes,
    _id: state.notesReducer._id,
    newNote: state.notesReducer.newNote
});

export default connect(mapStatetoProps)(Note);
