import axios from "axios";

export const getNotes = () =>({
  type: 'GET_NOTES',
})

export const addNote = () =>({
  type: 'ADD_NOTE',
})

export const deleteItem = () =>({
  type: 'DELETE_ITEM'
})


//getState will get you the entire redux state
export const listNotes = () => (dispatch, getState) => {
  // this returns a function
  axios
    .get("/list")
    .then(res => dispatch(setListNotes(res.data)))
    .then(console.log);
};

export const setListNotes = notes => ({
  type: "SET_LIST_NOTES",
  notes
});

export const setId = _id => ({
  type: "SET_ID",
  _id
});

export const setNewNote = newNote => ({
  type: "SET_NEW_NOTE",
  newNote
});

export const updateNote = () => (dispatch, getState) => {
  const { _id, newNote } = getState().notesReducer;

  axios.get(`/update?_id=${_id}&notes=${newNote}`)
    .then(() => {
      dispatch(setNewNote(""));
      dispatch(setId(""));
      dispatch(listNotes());
    })
    .catch(console.log);
};
