import React, { useState } from "react";
import { connect } from "react-redux";
import { Redirect } from "react-router-dom";
import md5 from "md5";
import axios from "axios";
import { setIsLoggedIn, setUsername, setPassword } from "../redux/actions/usersActions";

const options = {
    withCredentials: true
};

const Login = ({ dispatch, username, password, isLoggedIn }) => {

    React.useEffect(() => {
        axios.get('/notes/', options)
          .then((res) => {
            console.log(res)
          })
          .catch(console.log);
      }, []); // VERY IMPORTANT NEEDS THE EMPTY ARRAY

    const validate = (e) => {
        e.preventDefault()
        const body = {
            username,
            password: md5(password)
        };
        axios.post("/authserver/", body, options).then(res => {
            if (res.data.valid) {
                document.cookie = `username=${username}`;
                document.cookie = `password=${md5(password)}`;
                dispatch(setIsLoggedIn(true));
            } else {
                document.cookie = `username=`;
                document.cookie = `password=`;
                dispatch(setIsLoggedIn(false));
                console.log("Invalid user credentials");
            }
        })
        .catch(console.log())
    };

    return (
        <div>
            {isLoggedIn && <Redirect to="/" />}
            <form>
                <div>
                    <p>Username:</p>
                    <input name="username" type="text" onChange={(e)=>dispatch(setUsername(e.target.value))}></input>
                </div>

                <div>
                    <p>Password</p>
                    <input name="password" type="text" onChange={(e)=>dispatch(setPassword(e.target.value))}></input>
                </div>

                <button onClick={validate} type="button">
                    LOGIN
                </button>
            </form>
        </div>
    );
};

const mapStateToProps = state => ({
    username: state.userReducer.username,
    password: state.userReducer.password,
    isLoggedIn: state.userReducer.isLoggedIn
});

export default connect(mapStateToProps)(Login);
