import axios from "axios";
const proxyUrl = "http://localhost:3004";

export const getNotes = () => (dispatch, getState) => {
  axios
    .get(`${proxyUrl}/allNotes`)
    .then(res => {
      dispatch(setListNotes(res.data));
    })
    .then(console.log);
};

export const addNote = note => (dispatch, getState) => {
  axios
    .get(`${proxyUrl}/addNote`,note,getState)
    .then(res => {
      dispatch({
        type: "ADD_NOTE",
        note: res.data
      });
    })
    .then(console.log);
};

export const setListNotes = notes => ({
  type: "SET_LIST_NOTES",
  notes
});

export const setId = _id => ({
  type: "SET_ID",
  _id
});

export const setNewNote = newNote => ({
  type: "SET_NEW_NOTE",
  newNote
});

export const updateNote = () => (dispatch, getState) => {
  const { _id, newNote } = getState().notesReducer;

  axios
    .get(`/update?_id=${_id}&notes=${newNote}`)
    .then(() => {
      dispatch(setNewNote(""));
      dispatch(setId(""));
      dispatch(getNotes());
    })
    .catch(console.log);
};
