import React,{useState} from 'react'
import {connect} from 'react-redux'
import {Redirect} from 'react-router-dom'
import {setUsername, setPassword, setIsLoggedIn,} from '../redux/actions/usersActions'
import md5 from 'md5'
import axios from 'axios'

const option = {
    withCredentials: true,
}

const Signup = ({dispatch, username,password, isLoggedIn}) =>{

    const handleOnSubmit = (e) =>{
        e.preventDefault()

        const body = ({
            username,
            password: md5(password)
        })

        axios.post('/addUser',body,option)
        .then(res=>{
            if(res.data.valid){
                document.cookie = `username=${username}`
                document.cookie = `password=${password}`
                dispatch(setIsLoggedIn(true))
            }
        })
    }

    return(
        <div>
            {isLoggedIn && <Redirect to='/' />}
            <p>Create an account!</p>
             <form onSubmit={handleOnSubmit}>
                <label for="">Username:</label>
                <input onChange={(e)=>dispatch(setUsername(e.target.value))} name = "username" type="text"></input>
                
                <label for="">Password:</label>
                <input onChange = {(e)=>dispatch(setPassword(e.target.value))} name = "password" type="password"></input>

                <button type="submit">JOIN</button>
            </form>
        </div>
    )
}

const mapStateToProps = (state) =>({
    isLoggedIn: state.userReducer.isLoggedIn,
    username: state.userReducer.username,
    password: state.userReducer.password
})

export default connect(mapStateToProps)(Signup);
