import React,{useState} from 'react'
import {connect} from 'react-redux'
import {setUsername, setIsLoggedIn, addUser} from '../redux/actions/usersActions'
import md5 from 'md5'


const Signup = ({dispatch, username,password, isLoggedIn}) =>{
    const handleOnSubmit = (e) =>{
        e.preventDefault()

        const {username,password} = ""

        const newUser = {
            username,
            password
        }

        dispatch(addUser(newUser))
    }

    return(
        <div>
            <p>Create an account!</p>
             <form onSubmit={handleOnSubmit}>
                <label for="">Username:</label>
                <input name = "username" type="text"></input>
                
                <label for="">Password:</label>
                <input name = "password" type="password"></input>

                <label for="">Re-enter password:</label>
                <input type="password"></input>

                <button type="submit">JOIN</button>
            </form>
        </div>
    )
}

const mapStateToProps = (state) =>({
    isLoggedIn: state.actionReducer.isLoggedIn,
    username: state.actionReducer.username,
    password: state.actionReducer.password
})

export default connect(mapStateToProps)(Signup);
