import React, { useState } from "react";
import { connect } from "react-redux";
import { Redirect } from "react-router-dom";
import md5 from "md5";
import axios from "axios";
import { setIsLoggedIn } from "../redux/actions/usersActions";

const options = {
    withCredentials: true
};

const Login = ({ dispatch, username, password, isLoggedIn }) => {
    const validate = () => {
        const body = {
            username,
            password
        };
        axios.post("/authserver/", body, options).then(res => {
            if (res.data.valid) {
                document.cookie = `username=${username}`;
                document.cookies = `password=${password}`;
                dispatch(setIsLoggedIn(true));
            } else {
                dispatch(setIsLoggedIn(false));
                console.log("Invalid user credentials");
            }
        });
    };

    return (
        <div>
            {isLoggedIn && <Redirect to="/" />}
            <form>
                <div>
                    <p>Username:</p>
                    <input name="username" type="text"></input>
                </div>

                <div>
                    <p>Password</p>
                    <input name="password" type="text"></input>
                </div>

                <button onClick={validate} type="button">
                    LOGIN
                </button>
            </form>
        </div>
    );
};

const mapStateToProps = state => ({
    username: state.userReducer.username,
    password: state.userReducer.password,
    isLoggedIn: state.userReducer.isLoggedIn
});

export default connect(mapStateToProps)(Login);
